{% block body %}
  <div>
  </div>
  <div id=\"quizbox\">
  {% for product in products if product.idCategorie == id %}
  <div class=\"productbox\">
      {{ product.name }}<br>
  </div>
  {% endfor %}
  </div>
{% endblock %}
